<?phpnamespace Nemundo\Content\App\Calendar\Install;use Nemundo\App\Application\Setup\ApplicationSetup;use Nemundo\App\Application\Type\Install\AbstractInstall;use Nemundo\App\WebService\Setup\ServiceRequestSetup;use Nemundo\Content\Action\Setup\ActionSetup;use Nemundo\Content\App\Calendar\Action\CalendarIndexAction;use Nemundo\Content\App\Calendar\Application\CalendarApplication;use Nemundo\Content\App\Calendar\Content\Calendar\CalendarContentType;use Nemundo\Content\App\Calendar\Data\CalendarModelCollection;use Nemundo\Content\App\Calendar\Service\CalendarPostService;use Nemundo\Content\App\Calendar\Setup\CalendarSourceSetup;use Nemundo\Content\App\Timeline\Application\TimelineApplication;use Nemundo\Content\Data\ContentType\ContentTypeReader;use Nemundo\Content\Index\Calendar\DateTimeIndexTrait;use Nemundo\Content\Setup\ContentTypeSetup;use Nemundo\Model\Setup\ModelCollectionSetup;class CalendarInstall extends AbstractInstall{    public function install()    {        //(new TimelineApplication())->installApp();        /*(new ApplicationSetup())            ->addApplication(new CalendarApplication());*/        (new ModelCollectionSetup())            ->addCollection(new CalendarModelCollection());        (new ActionSetup())            ->addContentAction(new CalendarIndexAction());        $reader = new ContentTypeReader();        foreach ($reader->getData() as $contentTypeRow) {            //if ($contentTypeRow->getContentType()->isObjectOfTrait(DateTimeIndexTrait::class)) {            $item = $contentTypeRow->getContentType()->getItem(0);            if ($item->isObjectOfTrait(DateTimeIndexTrait::class)) {                (new CalendarSourceSetup())->addSourceContentType($contentTypeRow->getContentType());            }        }        /*        (new ContentTypeSetup(new CalendarApplication()))            ->addContentType(new CalendarContentType());        (new ServiceRequestSetup(new CalendarApplication()))            ->addService(new CalendarPostService());*/        //->addContentType(new CalendarWidgetContentType());    }}