<?phpnamespace Nemundo\Content\App\Camera\Content\Camera;use Nemundo\Content\App\File\Type\ImageIndexTrait;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Core\Http\Request\File\FileRequest;use Nemundo\Html\Form\Input\AcceptFileType;use Nemundo\Html\Form\Input\FileInput;class CameraForm extends AbstractContentForm{    /**     * @var CameraType     */    //public $contentType;    /**     * @var FileInput     */    private $file;    private $requestName = 'cam_input';    public function getContent()    {        $this->file = new FileInput($this);        $this->file->accept = AcceptFileType::IMAGE;        $this->file->capture = 'camera';        $this->file->name = $this->requestName;        return parent::getContent();    }    protected function onSave()    {        // TODO: Implement onSave() method.        $fileRequest=new FileRequest($this->requestName);        $builder=new CameraBuilder();        $builder->image->fromFileRequest($fileRequest);        $builder->addActionList($this->getActionList());        $builder->buildContent();    }    /*    protected function onSubmit()    {        $fileRequest=new FileRequest($this->requestName);        (new CameraContentImport())->fromFileRequest($fileRequest);       /* $this->contentType->image = new FileRequest($this->requestName);        $this->contentType->saveType();*/    //}}