<?phpnamespace Nemundo\Content\App\Camera\Content\Camera\View;use Nemundo\Admin\Com\Button\AdminIconSiteButton;use Nemundo\Admin\Com\Image\AdminImage;use Nemundo\Admin\Com\Table\AdminLabelValueTable;use Nemundo\Content\App\Camera\Content\Camera\CameraItem;use Nemundo\Content\App\Camera\Parameter\CameraParameter;use Nemundo\Content\App\Camera\Site\ImageDownloadSite;use Nemundo\Content\View\AbstractContentView;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;class CameraDetailView extends AbstractContentView{    protected function loadView()    {        $this->viewName = 'Detail Information';        $this->viewId = '748dbb1b-6ac9-41c7-afac-4741c728e98c';    }    public function getContent()    {        $cameraRow = (new CameraItem($this->dataId))->getDataRow();        $img = new AdminImage($this);        $img->src = $cameraRow->image->getImageUrl($cameraRow->model->imageAutoSize1200);        $table = new AdminLabelValueTable($this);        $table->addLabelValue('Camera', $cameraRow->camera);        if ($cameraRow->hasGeoCoordinate) {            $table->addLabelValue($cameraRow->model->geoCoordinate->label, $cameraRow->geoCoordinate->getText());        }        return parent::getContent();    }}