<?phpnamespace Nemundo\Content\App\Camera\Content\Camera\View;use Nemundo\Admin\Com\Button\AdminIconSiteButton;use Nemundo\Admin\Com\Image\AdminImage;use Nemundo\Admin\Com\Table\AdminLabelValueTable;use Nemundo\Content\App\Camera\Content\Camera\CameraItem;use Nemundo\Content\App\Camera\Parameter\CameraParameter;use Nemundo\Content\App\Camera\Site\ImageDownloadSite;use Nemundo\Content\View\AbstractContentView;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;class CameraView extends AbstractContentView{    protected function loadView()    {        $this->viewName = 'default';        $this->viewId = '862cb51f-6a24-425a-9b94-bfc077394b23';        $this->defaultView = true;    }    public function getContent()    {        $cameraRow = (new CameraItem($this->dataId))->getDataRow();        $img = new AdminImage($this);        $img->src = $cameraRow->image->getImageUrl($cameraRow->model->imageAutoSize1200);        return parent::getContent();    }}