<?phpnamespace Nemundo\Content\App\Contact\Content\Contact;use Nemundo\Admin\Com\Button\AdminIconSiteButton;use Nemundo\Admin\Com\Button\AdminSiteButton;use Nemundo\Com\Html\Hyperlink\EmailHyperlink;use Nemundo\Com\Html\Hyperlink\PhoneHyperlink;use Nemundo\Content\App\Contact\Parameter\ContactParameter;use Nemundo\Content\App\Contact\Site\BusinessCardSite;use Nemundo\Content\View\AbstractContentView;use Nemundo\Html\Formatting\Small;use Nemundo\Html\Heading\H2;use Nemundo\Html\Paragraph\Paragraph;class ContactView extends AbstractContentView{    /**     * @var ContactType     */    public $contentType;    protected function loadView()    {        $this->viewName='default';        $this->viewId='c3f71c35-2c81-4a7c-95fb-45add5bb66fa';        $this->defaultView=true;    }    public function getContent()    {        $contactRow = (new ContactItem($this->dataId))->getDataRow();  // $this->contentType->getDataRow();        /*$p = new Small($this);        $p->content = 'Contact';*/        $h2 = new H2($this);        $h2->content = $contactRow->company;        $h2 = new H2($this);        $h2->content = $contactRow->lastName . ' ' . $contactRow->firstName;        $p=new Paragraph($this);        $phone = new PhoneHyperlink($p);        $phone->phone = $contactRow->phone;        $p=new Paragraph($this);        $email = new EmailHyperlink($p);        $email->email = $contactRow->email;        $p=new Paragraph($this);        $btn = new AdminIconSiteButton($this);        $btn->site = clone(BusinessCardSite::$site);        $btn->site->addParameter(new ContactParameter($contactRow->id));        return parent::getContent();    }}