<?phpnamespace Nemundo\Content\App\Contact\Content\Email;use Nemundo\Admin\Com\ListBox\AdminEmailBox;use Nemundo\Content\Form\AbstractContentForm;class EmailForm extends AbstractContentForm{    /**     * @var EmailType     */    //public $contentType;    /**     * @var AdminEmailBox     */    private $email;    public function getContent()    {        $this->email = new AdminEmailBox($this);        $this->email->validation = true;        return parent::getContent();    }    protected function loadUpdateForm()    {        $this->email->value=(new EmailItem($this->dataId))->getDataRow()->email;    }    protected function onSave()    {        $builder = new EmailBuilder($this->dataId);        $builder->email = $this->email->getValue();        $builder->addActionList($this->getActionList());        $builder->buildContent();    }}