<?phpnamespace Nemundo\Content\App\Contact\Content\Email;use Nemundo\Com\Html\Hyperlink\ProtectedEmailHyperlink;use Nemundo\Content\View\AbstractContentView;class EmailView extends AbstractContentView{    /**     * @var EmailType     */    public $contentType;    protected function loadView()    {        $this->viewName = 'default';        $this->viewId = '99df6ba7-adb4-4980-b572-7fda0f4a36d3';        $this->defaultView = true;    }    public function getContent()    {        $emailRow = (new EmailItem($this->dataId))->getDataRow();        $hyperlink = new ProtectedEmailHyperlink($this);        $hyperlink->email = $emailRow->email;        return parent::getContent();    }}