<?phpnamespace Nemundo\Content\App\Contact\Content\Phone;use Nemundo\Admin\Com\ListBox\AdminTextBox;use Nemundo\Content\Form\AbstractContentForm;class PhoneForm extends AbstractContentForm{    /**     * @var AdminTextBox     */    private $phone;    public function getContent()    {        $this->phone = new AdminTextBox($this);        $this->phone->label = 'Phone';        return parent::getContent();    }    protected function loadUpdateForm()    {        $this->phone->value = (new PhoneItem($this->dataId))->getDataRow()->phone;    }    protected function onSave()    {        $builder = new PhoneBuilder($this->dataId);        $builder->phone = $this->phone->getValue();        $builder->addActionList($this->getActionList());        $builder->buildContent();    }}