<?phpnamespace Nemundo\Content\App\Contact\Content\Phone\View;use Nemundo\Com\Html\Hyperlink\PhoneHyperlink;use Nemundo\Content\View\AbstractContentView;class PhoneView extends AbstractContentView{    protected function loadView()    {        $this->viewName = 'Button';        $this->viewId = '';        $this->defaultView = true;    }    public function getContent()    {        $phoneRow=(new PhoneItem($this->dataId))->getDataRow();        $phone = new PhoneHyperlink($this);        $phone->phone = $phoneRow->phone;  // $this->contentType->getDataRow()->phone;        return parent::getContent();    }}