<?phpnamespace Nemundo\Content\App\Contact\Site;use Nemundo\Admin\Site\AbstractBusinessCardSite;use Nemundo\Content\App\Contact\Parameter\ContactParameter;use Nemundo\Format\Vcard\VcardResponse;use Nemundo\Web\Site\AbstractSite;class BusinessCardSite extends AbstractBusinessCardSite  // AbstractSite{    /**     * @var BusinessCardSite     */    public static $site;    protected function loadSite()    {        parent::loadSite();        /*$this->title = 'BusinessCard';        $this->url = 'business-card';        $this->menuActive = false;*/        BusinessCardSite::$site = $this;    }    public function loadContent()    {        $contactRow = (new ContactParameter())->getContentItem()->getDataRow();        $response = new VcardResponse();        $response->firstName = $contactRow->firstName;        $response->lastName = $contactRow->lastName;        $response->phone = $contactRow->phone;        $response->email = $contactRow->email;        $response->sendResponse();    }}