<?phpnamespace Nemundo\Content\App\Dashboard\Action;use Nemundo\Content\Action\AbstractActionForm;use Nemundo\Content\App\Dashboard\Com\ListBox\DashboardListBox;use Nemundo\Content\Form\AbstractContentForm;class DashboardActionContentForm extends AbstractActionForm{    /**     * @var DashboardListBox     */    private $dashboard;    public function getContent()    {        $this->dashboard = new DashboardListBox($this);        $this->dashboard->validation = true;        return parent::getContent();    }    protected function onSubmit()    {        $action=new DashboardContentAction();        $action->dashboardId=$this->dashboard->getValue();        $action->onAction($this->content);        /*$this->contentType->dashboardId=$this->dashboard->getValue();        $this->contentType->onAction();*/    }}