<?phpnamespace Nemundo\Content\App\Event\Site;use Nemundo\Admin\Site\AbstractCalendarSite;use Nemundo\Content\App\Calendar\Content\Calendar\CalendarContentType;use Nemundo\Content\App\Calendar\Parameter\CalendarParameter;use Nemundo\Content\App\Event\Content\Event\EventItem;use Nemundo\Content\Parameter\DataIdParameter;use Nemundo\Format\Calendar\VCalendarResponse;use Nemundo\Office\Outlook\Vcalendar;use Nemundo\Package\FontAwesome\IconSite\AbstractCalendarIconSite;use Nemundo\Web\Site\AbstractSite;class VcardSite extends AbstractCalendarSite{    /**     * @var VcardSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Vcalendar';        $this->url = 'vcalendar';        VcardSite::$site = $this;    }    public function loadContent()    {        $eventId = (new DataIdParameter())->getValue();        //$calendarParameter=new CalendarParameter();        //$calendarRow = (new CalendarContentType($calendarParameter->getValue()))->getDataRow();        //$calendarRow = (new CalendarIndexReader())->getRowById($calendarParameter->getValue());        $eventRow = (new EventItem($eventId))->getDataRow();        $card = new VCalendarResponse();        $card->dateFrom = $eventRow->dateTime->getDate();        $card->dateTo = $eventRow->dateTime->getDate();        $card->label = $eventRow->event;        $card->sendResponse();    }}