<?phpnamespace Nemundo\Content\App\Explorer\Content\Container;use Nemundo\Admin\Com\ListBox\AdminLargeTextBox;use Nemundo\Admin\Com\ListBox\AdminTextBox;use Nemundo\Content\App\Explorer\Data\Container\ContainerModel;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Package\Bootstrap\FormElement\BootstrapLargeTextBox;use Nemundo\Package\Bootstrap\FormElement\BootstrapTextBox;class ContainerForm extends AbstractContentForm{    /**     * @var BootstrapTextBox     */    private $container;    /**     * @var BootstrapLargeTextBox     */    private $description;    private $icon;    public function getContent()    {        $model = new ContainerModel();        $this->container = new AdminTextBox($this);        $this->container->label = $model->container->label;        $this->container->validation = true;        $this->description = new AdminLargeTextBox($this);        $this->description->label = $model->description->label;        return parent::getContent();    }    protected function loadUpdateForm()    {        $containerRow = (new ContainerItem($this->dataId))->getDataRow();        $this->container->value = $containerRow->container;        $this->description->value = $containerRow->description;    }    protected function onSave()    {        $builder = new ContainerBuilder($this->dataId);        $builder->container = $this->container->getValue();        $builder->description = $this->description->getValue();        $builder->addActionList($this->getActionList());        $builder->buildContent();    }}