<?phpnamespace Nemundo\Content\App\Favorite\Action;use Nemundo\Content\Action\AbstractActionView;use Nemundo\Content\App\Favorite\Com\Button\FavoriteButton;use Nemundo\Html\Paragraph\Paragraph;use Nemundo\User\Session\UserSession;class FavoriteActionView extends AbstractActionView{    public function getContent()    {        if ((new UserSession())->isUserLogged()) {            $btn = new FavoriteButton($this);            $btn->contentId = $this->contentId;        } else {            $p = new Paragraph($this);            $p->content = 'No User logged';        }        return parent::getContent();    }}