<?phpnamespace Nemundo\Content\App\Favorite\Action;use Nemundo\Content\Action\AbstractContentAction;use Nemundo\Content\App\Favorite\Data\Favorite\Favorite;use Nemundo\Content\Type\AbstractContentItem;use Nemundo\Core\Debug\Debug;use Nemundo\User\Session\UserSession;class FavoriteContentAction extends AbstractContentAction{    protected function loadAction()    {        $this->actionLabel = 'Favorite';        $this->actionId = 'e077f622-a1d7-4737-be3d-ada67c3cc8bc';        $this->viewClass = FavoriteActionView::class;    }    public function onAction(AbstractContentItem $item)    {        if ((new UserSession())->isUserLogged()) {            $data = new Favorite();            $data->updateOnDuplicate = true;            $data->contentId = $item->getContentId();            $data->userId = (new UserSession())->userId;            $data->subject = $item->getSubject();            $data->save();        } else {            (new Debug())->write('No User Logged');        }    }}