<?phpnamespace Nemundo\Content\App\Favorite\Com\Button;use Nemundo\Admin\Com\Button\AdminIconSiteButton;use Nemundo\Html\Container\AbstractHtmlContainer;use Nemundo\Content\App\Favorite\Data\Favorite\FavoriteCount;use Nemundo\Content\App\Favorite\Site\FavoriteDeleteSite;use Nemundo\Content\App\Favorite\Site\FavoriteSaveSite;use Nemundo\Content\Parameter\ContentParameter;use Nemundo\Content\Type\AbstractContentType;use Nemundo\Html\Paragraph\Paragraph;use Nemundo\User\Session\UserSession;class FavoriteButton extends AbstractHtmlContainer{    /**     * @var AbstractContentType     */    //public $contentType;    public $contentId;    public function getContent()    {        $p=new Paragraph($this);        $p->content='Favorite View';        //$contentId = $this->contentType->getContentId();        $contentParameter = new ContentParameter($this->contentId);        $favoriteCount = new FavoriteCount();        $favoriteCount->filter->andEqual($favoriteCount->model->contentId, $this->contentId);        $favoriteCount->filter->andEqual($favoriteCount->model->userId, (new UserSession())->userId);//        $button=null;        $button = new AdminIconSiteButton($this);        if ($favoriteCount->getCount() == 0) {  //          $button = new AdminIconSiteButton($this);            $button->site = FavoriteSaveSite::$site;            $button->site->addParameter($contentParameter);        } else {    //        $button = new AdminIconSiteButton($this);            $button->site = FavoriteDeleteSite::$site;            $button->site->addParameter($contentParameter);        }        $button->showTitle=false;        //$button->title = $this->contentType->getSubject();        return parent::getContent();    }}