<?phpnamespace Nemundo\Content\App\Favorite\Install;use Nemundo\App\Application\Type\Install\AbstractUninstall;use Nemundo\Content\Action\Setup\ActionSetup;use Nemundo\Content\App\Favorite\Action\FavoriteContentAction;use Nemundo\Content\App\Favorite\Data\FavoriteModelCollection;use Nemundo\Model\Setup\ModelCollectionSetup;class FavoriteUninstall extends AbstractUninstall{    public function uninstall()    {        (new ActionSetup())            ->removeContentAction(new FavoriteContentAction());        (new ModelCollectionSetup())            ->removeCollection(new FavoriteModelCollection());    }}