<?phpnamespace Nemundo\Content\App\Favorite\Site;use Nemundo\Admin\Site\AbstractAdminIconSite;use Nemundo\Content\App\Favorite\Action\FavoriteContentAction;use Nemundo\Content\Builder\ContentBuilder;use Nemundo\Content\Parameter\ContentParameter;use Nemundo\Core\Http\Url\UrlReferer;class FavoriteSaveSite extends AbstractAdminIconSite  // AbstractIconSite // AbstractIconSite{    /**     * @var FavoriteSaveSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Favorite Save';        $this->url = 'favorite-save';        $this->menuActive = false;        $this->iconName = 'star';  // 'star-half-stroke';   //= new EmptyFavoriteIcon();        //<i class="fa-light fa-star-half-stroke"></i>        new FavoriteDeleteSite($this);        FavoriteSaveSite::$site = $this;    }    public function loadContent()    {        $contentParameter = new ContentParameter();        $contentParameter->contentTypeCheck = false;        $builder = new ContentBuilder($contentParameter->getValue());        $action = new FavoriteContentAction();        $action->onAction($builder->getContentItem());        (new UrlReferer())->redirect();    }}