<?phpnamespace Nemundo\Content\App\Feed\Content\Article;use Nemundo\Content\App\Feed\Data\Article\ArticleReader;use Nemundo\Content\App\Feed\Data\Article\ArticleRow;use Nemundo\Content\App\Feed\Parameter\FeedItemParameter;use Nemundo\Content\App\Feed\Site\FeedItemRedirectSite;use Nemundo\Content\App\Timeline\Index\TimelineIndexTrait;use Nemundo\Content\Index\News\NewsIndexTrait;use Nemundo\Content\Index\Tree\Com\Form\ContentSearchForm;use Nemundo\Content\Type\AbstractContentItem;use Nemundo\Content\Type\AbstractContentType;use Nemundo\Content\Type\AbstractSearchContentType;use Nemundo\Content\Type\JsonContentTrait;use Nemundo\Core\Type\DateTime\DateTime;class ArticleItem extends AbstractContentItem{    protected function loadItem()    {        $this->contentType=new ArticleType();    }    protected function onDataRow()    {        $reader = new ArticleReader();        $reader->model->loadFeed();        $this->dataRow = $reader->getRowById($this->dataId);    }    /**     * @return \Nemundo\Model\Row\AbstractModelDataRow|ArticleRow     */    public function getDataRow()    {        return parent::getDataRow();    }    public function getSubject()    {        return $this->getDataRow()->title;    }    public function getText()    {        return $this->getDataRow()->description;    }    public function getDateTime()    {        return $this->getDataRow()->dateTime;    }    public function getUniqueId()    {        return $this->getDataRow()->uniqueId;    }    public function getTitle()    {        return $this->getDataRow()->title;    }    public function getLead()    {        return $this->getDataRow()->description;    }    public function getUrl()    {        return $this->getDataRow()->url;    }    public function getSource()    {        return $this->getDataRow()->feed->title;    }    public function getSourceUniqueId()    {        return $this->getDataRow()->feedId;        // TODO: Implement getSourceUniqueId() method.    }    public function getData()    {        $row = $this->getDataRow();        $data = [];        $data['title'] = $row->title;        $data['description'] = $row->description;        $data['url'] = $row->url;        return $data;    }}