<?phpnamespace Nemundo\Content\App\Feed\Content\Article;use Nemundo\Admin\Com\Pagination\AdminPagination;use Nemundo\Admin\Com\Table\AdminClickableTable;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\Com\TableBuilder\TableHeader;use Nemundo\Content\App\Feed\Data\Article\ArticlePaginationReader;use Nemundo\Content\App\Feed\Data\FeedItem\FeedItemPaginationReader;use Nemundo\Content\View\AbstractContentListing;use Nemundo\Db\Sql\Order\SortOrder;use Nemundo\Package\Bootstrap\Pagination\BootstrapPagination;use Nemundo\Package\Bootstrap\Table\BootstrapClickableTableRow;class ArticleListing extends AbstractContentListing{    public function getContent()    {        // search        // feed, type (audio/video/blog)        // date (from/to)        $table = new AdminTable($this);        $itemReader = new ArticlePaginationReader();        $itemReader->model->loadFeed();        $itemReader->addOrder($itemReader->model->dateTime,SortOrder::DESCENDING);        //$itemReader->paginationLimit = ProcessConfig::PAGINATION_LIMIT;        $header=new TableHeader($table);        $header->addText($itemReader->model->title->label);        $header->addText($itemReader->model->description->label);        $header->addText($itemReader->model->dateTime->label);        $header->addText('Source');        foreach ($itemReader->getData() as $itemRow) {            $row = new AdminTableRow($table);            $row->addText($itemRow->title);            $row->addText($itemRow->description);            $row->addText($itemRow->dateTime->getShortDateTimeLeadingZeroFormat());            $row->addText($itemRow->feed->title);            /*$contentType = new ArticleType($itemRow->id);            $row->addClickableSite($contentType->getViewSite());*/        }        $pagination = new AdminPagination($this);        $pagination->paginationReader = $itemReader;        return parent::getContent();    }}