<?phpnamespace Nemundo\Content\App\Feed\Content\Feed;use Nemundo\Admin\Com\Pagination\AdminPagination;use Nemundo\Admin\Com\Table\AdminClickableTable;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\Com\TableBuilder\TableHeader;use Nemundo\Content\App\Feed\Data\Feed\FeedPaginationReader;use Nemundo\Content\View\AbstractContentAdmin;class FeedAdmin extends AbstractContentAdmin{    protected function onIndex()    {        $feedReader = new FeedPaginationReader();        $feedReader->addOrder($feedReader->model->title);        $table = new AdminTable($this);        $header = new TableHeader($table);        $header->addText($feedReader->model->title->label);        $header->addText($feedReader->model->feedUrl->label);        //$header->addText($feedReader->model->dateTime->label);//        $header->addText('Source');        $header->addEmpty();        foreach ($feedReader->getData() as $itemRow) {            $row = new AdminTableRow($table);            $row->addText($itemRow->title);            $row->addText($itemRow->feedUrl);            //$row->addText($itemRow->dateTime->getShortDateTimeLeadingZeroFormat());            //$row->addText($itemRow->feed->title);            //$contentType = new FeedItemContentType($itemRow->id);            //$row->addClickableSite($contentType->getViewSite());            $row->addIconActionSite($this->getDeleteSite($itemRow->id));        }        $pagination = new AdminPagination($this);        $pagination->paginationReader = $feedReader;    }}