<?phpnamespace Nemundo\Content\App\Feed\Content\Feed;use Nemundo\Admin\Com\ListBox\AdminTextBox;use Nemundo\Content\Form\AbstractContentForm;class FeedForm extends AbstractContentForm{    /**     * @var AdminTextBox     */    private $rssUrl;    public function getContent()    {        $this->rssUrl = new AdminTextBox($this);        $this->rssUrl->label = 'RSS Url';        $this->rssUrl->validation = true;        // multi line        // check if valid rss        return parent::getContent();    }    protected function onSave()    {        $builder = new FeedBuilder($this->dataId);        $builder->rssUrl = $this->rssUrl->getValue();        $builder->addActionList($this->getActionList());        $builder->buildContent();    }}