<?phpnamespace Nemundo\Content\App\Feed\Content\Feed;use Nemundo\Admin\Com\Image\AdminImage;use Nemundo\Admin\Com\Pagination\AdminPagination;use Nemundo\Admin\Com\Table\AdminClickableTable;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\Com\TableBuilder\TableHeader;use Nemundo\Content\App\Feed\Data\Feed\FeedPaginationReader;use Nemundo\Content\View\AbstractContentListing;use Nemundo\Db\Sql\Order\SortOrder;use Nemundo\Package\Bootstrap\Pagination\BootstrapPagination;use Nemundo\Package\Bootstrap\Table\BootstrapClickableTableRow;class FeedListing extends AbstractContentListing{    public function getContent()    {        $table = new AdminTable($this);        $feedReader = new FeedPaginationReader();        $feedReader->addOrder($feedReader->model->title);        $header=new TableHeader($table);        $header->addText($feedReader->model->title->label);        $header->addText($feedReader->model->description->label);        foreach ($feedReader->getData() as $feedRow) {            $row = new AdminTableRow($table);            $img=new AdminImage($row);            $img->src=$feedRow->feedImage->getUrl();  // getImageUrl($feedReader->model->im)            $row->addText($feedRow->title);            $row->addText($feedRow->description);            /*$contentType = new ArticleContentType($itemRow->id);            $row->addClickableSite($contentType->getViewSite());*/        }        $pagination = new AdminPagination($this);        $pagination->paginationReader = $feedReader;        return parent::getContent();    }}