<?phpnamespace Nemundo\Content\App\Feed\Page;use Nemundo\Admin\Com\Form\AdminSearchForm;use Nemundo\Admin\Com\Image\AdminImage;use Nemundo\Admin\Com\Item\AdminItemContainer;use Nemundo\Admin\Com\Item\AdminItemSmall;use Nemundo\Admin\Com\Item\AdminItemText;use Nemundo\Admin\Com\Item\AdminItemTitle;use Nemundo\Admin\Com\Item\AdminItemUrlHyperlink;use Nemundo\Admin\Com\Pagination\AdminPagination;use Nemundo\Admin\Style\AdminStyle;use Nemundo\Content\App\Feed\Com\ListBox\FeedListBox;use Nemundo\Content\App\Feed\Data\Article\ArticlePaginationReader;use Nemundo\Content\App\Feed\Template\FeedTemplate;use Nemundo\Db\Sql\Order\SortOrder;class FeedPage extends FeedTemplate{    public function getContent()    {        $form = new AdminSearchForm($this);        $feed = new FeedListBox($form);        $feed->searchMode = true;        $feed->submitOnChange = true;        $container = new AdminItemContainer($this);        $itemReader = new ArticlePaginationReader();        $itemReader->model->loadFeed();        if ($feed->hasValue()) {            $itemReader->filter->andEqual($itemReader->model->feedId, $feed->getValue());        }        $itemReader->addOrder($itemReader->model->dateTime, SortOrder::DESCENDING);        $itemReader->paginationLimit = 50;        foreach ($itemReader->getData() as $itemRow) {            //$container=new AdminItemContainer($this);            $hyperlink = new AdminItemUrlHyperlink($container);            $hyperlink->openNewWindow = true;            $hyperlink->url = $itemRow->url;            $hyperlink->addCssClass(AdminStyle::$SPACE);            $small = new AdminItemSmall($hyperlink);            $small->content = $itemRow->feed->title;            $title = new AdminItemTitle($hyperlink);            $title->content = $itemRow->title;            if ($itemRow->hasImage) {                $image = new AdminImage($hyperlink);                $image->src = $itemRow->imageUrl;            }            $text = new AdminItemText($hyperlink);            $text->content = $itemRow->description;            $small = new AdminItemSmall($hyperlink);            $small->content = $itemRow->dateTime->getShortDateTimeLeadingZeroFormat();        }        $pagination = new AdminPagination($this);        $pagination->paginationReader = $itemReader;        return parent::getContent();    }}