<?phpnamespace Nemundo\Content\App\Feed\Site\Admin;use Nemundo\Admin\Site\AbstractDeleteIconSite;use Nemundo\Content\App\Feed\Content\Feed\FeedRemove;use Nemundo\Content\App\Feed\Content\Feed\FeedType;use Nemundo\Content\App\Feed\Parameter\FeedParameter;use Nemundo\Core\Http\Url\UrlReferer;class FeedDeleteSite extends AbstractDeleteIconSite{    /**     * @var FeedDeleteSite     */    public static $site;    protected function loadSite()    {        parent::loadSite();        $this->url='feed-delete';        FeedDeleteSite::$site=$this;    }    public function loadContent()    {        $feedId=(new FeedParameter())->getValue();        // run as job        //(new FeedContentType($feedId))->deleteType();        (new FeedRemove($feedId))->removeItem();        (new UrlReferer())->redirect();    }}