<?phpnamespace Nemundo\Content\App\File\Content\Audio;use Nemundo\Admin\Com\ListBox\AdminFileUpload;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Html\Form\Input\AcceptFileType;class AudioForm extends AbstractContentForm{    /**     * @var AdminFileUpload     */    private $audio;    public function getContent()    {        $this->audio = new AdminFileUpload($this);        $this->audio->label = 'Audio';        $this->audio->multiUpload = true;        $this->audio->acceptFileType = AcceptFileType::AUDIO;        return parent::getContent();    }    protected function onSave()    {        foreach ($this->audio->getMultiFileRequest() as $fileRequest) {            $builder = new AudioBuilder();            $builder->audio->fromFileRequest($fileRequest);            $builder->addActionList($this->getActionList());            $builder->buildContent();        }    }}