<?phpnamespace Nemundo\Content\App\File\Content\Base\Form;use Nemundo\Admin\Com\ListBox\AdminTextBox;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Core\Validation\ValidationType;abstract class AbstractUrlForm extends AbstractContentForm{    public $formName = 'From Url';    /**     * @var AdminTextBox     */    protected $url;    public function getContent()    {        $this->url = new AdminTextBox($this);        $this->url->label = 'Url';        $this->url->validation = true;        $this->url->validationType = ValidationType::IS_URL;        return parent::getContent();    }}