<?phpnamespace Nemundo\Content\App\File\Content\Document\Form;use Nemundo\Admin\Com\ListBox\AdminFileUpload;use Nemundo\Content\App\File\Content\Document\DocumentBuilder;use Nemundo\Content\Form\AbstractContentForm;class DocumentForm extends AbstractContentForm{    /**     * @var AdminFileUpload     */    private $document;    public function getContent()    {        $this->document = new AdminFileUpload($this);        $this->document->label = 'File';        $this->document->multiUpload = true;        $this->document->acceptFileType = '.pdf,.txt';        return parent::getContent();    }    protected function onSave()    {        foreach ($this->document->getMultiFileRequest() as $fileRequest) {            $builder = new DocumentBuilder();            $builder->document->fromFileRequest($fileRequest);            $builder->addActionList($this->getActionList());            $builder->buildContent();        }    }}