<?phpnamespace Nemundo\Content\App\File\Content\Document\Form;use Nemundo\Admin\Com\ListBox\AdminTextBox;use Nemundo\Content\App\File\Content\Document\DocumentBuilder;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Core\Validation\ValidationType;class DocumentUrlForm extends AbstractContentForm{    public $formName = 'From Url';    /**     * @var AdminTextBox     */    private $url;    public function getContent()    {        $this->url = new AdminTextBox($this);        $this->url->label = 'Url';        $this->url->validation = true;        $this->url->validationType = ValidationType::IS_URL;        return parent::getContent();    }    protected function onSave()    {        $builder = new DocumentBuilder();        $builder->document->fromUrl($this->url->getValue());        $builder->addActionList($this->getActionList());        $builder->buildContent();    }}