<?phpnamespace Nemundo\Content\App\File\Content\Document\View;use Nemundo\Com\Html\Hyperlink\UrlHyperlink;use Nemundo\Content\App\File\Content\Document\DocumentItem;use Nemundo\Content\View\AbstractContentView;use Nemundo\Core\Type\Text\Html;use Nemundo\Html\Block\ContentDiv;class DocumentTextView extends AbstractContentView{    protected function loadView()    {        $this->viewName = 'Text';        $this->viewId = 'b5255b21-549a-4343-b1d8-e595ff89b975';    }    public function getContent()    {        $documentRow = (new DocumentItem($this->dataId))->getDataRow();        $hyperlink = new UrlHyperlink($this);        $hyperlink->content = $documentRow->document->getFilename();        $hyperlink->url = $documentRow->document->getUrl();        $div = new ContentDiv($this);        $div->content = (new Html($documentRow->text))->getValue();        return parent::getContent();    }}