<?phpnamespace Nemundo\Content\App\File\Content\Document\View;use Nemundo\Com\Html\Hyperlink\UrlHyperlink;use Nemundo\Content\App\File\Content\Document\DocumentItem;use Nemundo\Content\View\AbstractContentView;class DocumentView extends AbstractContentView{    protected function loadView()    {        $this->viewName = 'default';        $this->viewId = 'ca3cdc72-0909-4d56-81a0-6ffafe4236b7';        $this->defaultView = true;    }    public function getContent()    {        $documentRow = (new DocumentItem($this->dataId))->getDataRow();        $hyperlink = new UrlHyperlink($this);        $hyperlink->content = $documentRow->document->getFilename();        $hyperlink->url = $documentRow->document->getUrl();        $hyperlink->openNewWindow = true;        return parent::getContent();    }}