<?phpnamespace Nemundo\Content\App\File\Content\Image\Form;use Nemundo\Admin\Com\ListBox\AdminFileUpload;use Nemundo\Content\App\File\Content\Image\ImageBuilder;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Html\Form\Input\AcceptFileType;class ImageForm extends AbstractContentForm{    /**     * @var AdminFileUpload     */    private $image;    public function getContent()    {        $this->image = new AdminFileUpload($this);        $this->image->label = 'Image';        //$this->image->validation = true;        $this->image->multiUpload = true;  // false;        $this->image->acceptFileType = AcceptFileType::IMAGE;        return parent::getContent();    }    protected function loadUpdateForm()    {    }    protected function onSave()    {        foreach ($this->image->getMultiFileRequest() as $fileRequest) {            $builder = new ImageBuilder();            $builder->image->fromFileRequest($fileRequest);            $builder->addActionList($this->getActionList());            $builder->buildContent();        }    }}