<?phpnamespace Nemundo\Content\App\File\Content\Image\Form;use Nemundo\Admin\Com\Image\AdminImage;use Nemundo\Com\Html\Listing\UnorderedList;use Nemundo\Content\App\File\Content\Image\ImageItem;use Nemundo\Content\App\File\Data\Image\ImageReader;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Core\Http\Request\Post\PostRequest;use Nemundo\Html\Form\Button\SubmitButton;class ImageSearchForm extends AbstractContentForm{    protected $requestName = 'image_id';    public $formName = 'Image Search';    public function getContent()    {        $ul = new UnorderedList($this);        $reader = new ImageReader();        foreach ($reader->getData() as $imageRow) {            $btn = new SubmitButton($ul);            $btn->name = $this->requestName;            $btn->label = '';            $btn->value = $imageRow->id;            $img = new AdminImage($btn);            $img->src = $imageRow->image->getImageUrl($reader->model->imageAutoSize400);        }        $this->submitButton->visible = false;        return parent::getContent();    }    protected function onSave()    {        $value = (new PostRequest($this->requestName))->getValue();        $item = new ImageItem($value);        foreach ($this->getActionList() as $action) {            $action->onAction($item);        }    }}