<?phpnamespace Nemundo\Content\App\File\Content\Image\Form;use Nemundo\Content\App\File\Content\Base\Form\AbstractUrlForm;use Nemundo\Content\App\File\Content\Image\ImageBuilder;//ImageUrlFormclass UrlImageForm extends AbstractUrlForm{    protected function onSave()    {        $builder = new ImageBuilder();        $builder->image->fromUrl($this->url->getValue());        $builder->addActionList($this->getActionList());        $builder->buildContent();    }}