<?phpnamespace Nemundo\Content\App\File\Content\Image\View;use Nemundo\Admin\Com\Button\AdminButton;use Nemundo\Content\App\File\Content\Image\ImageType;use Nemundo\Content\View\AbstractContentView;use Nemundo\Html\Script\JavaScript;use Nemundo\Html\Script\JavaScriptType;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;use Nemundo\Package\Fancybox\FancyboxHyperlink;class ImageFancyboxContentView extends AbstractContentView{    /**     * @var ImageType     */    public $contentType;    protected function loadView()    {        $this->viewName='Fancybox';        $this->viewId='00a466d7-8bfd-41e1-8375-ae88e339ebdb';        $this->defaultView=true;    }    public function getContent()    {        $imageRow = $this->contentType->getDataRow();        $fancybox = new FancyboxHyperlink($this);        $fancybox->imageUrl = $imageRow->image->getImageUrl($imageRow->model->imageAutoSize1200);        $img = new BootstrapResponsiveImage($fancybox);        $img->src = $imageRow->image->getImageUrl($imageRow->model->imageAutoSize400);        $script=new JavaScript($this);        $script->src='/js/content_app/Image.js';        $script->type=JavaScriptType::MODULE;        $script->defer=true;        $btn=new AdminButton($this);        $btn->content='More';        $btn->addCssClass('content_image_btn');        $btn->addDataAttribute('image-url', $imageRow->image->getImageUrl($imageRow->model->imageAutoSize400));        $img = new BootstrapResponsiveImage($this);        $img->id='img2';        return parent::getContent();    }}