<?phpnamespace Nemundo\Content\App\File\Content\Upload;use Nemundo\Content\App\File\Content\Audio\AudioBuilder;use Nemundo\Content\App\File\Content\Audio\AudioType;use Nemundo\Content\App\File\Content\Document\DocumentBuilder;use Nemundo\Content\App\File\Content\Document\DocumentType;use Nemundo\Content\App\File\Content\File\FileContentType;use Nemundo\Content\App\File\Content\Image\ImageBuilder;use Nemundo\Content\App\File\Content\Image\ImageType;use Nemundo\Content\App\File\Content\Video\VideoBuilder;use Nemundo\Content\App\File\Content\Video\VideoType;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\File\FileInformation;use Nemundo\Model\Data\Property\File\FileProperty;class UploadFile extends AbstractBase{    /**     * @var FileProperty     */    public $file;    public $parentId;    public function __construct()    {        $this->file = new FileProperty();    }    public function uploadFile()    {        if ($this->file->hasFilename()) {            $filename = $this->file->getFilename();        }        if ($this->file->hasUrl()) {            $filename = $this->file->getUrl();        }        if ($this->file->hasFileRequest()) {            $filename = $this->file->getFileRequest()->filename;        }        $file = new FileInformation($filename);        //$type = new FileContentType();        if ($file->isText()||$file->isWord()||$file->isPdf()) {            //$type = new DocumentType();            //$file->isWord()            $builder = new DocumentBuilder();  // new ImageBuilder();  // new VideoBuilder();  // new VideoType();            $builder->document = $this->file;            $builder->buildContent();        }        /*if () {            $type = new DocumentType();        }        if () {            $type = new DocumentType();        }*/        if ($file->isImage()) {            //$type = new ImageType();            $builder = new ImageBuilder();  // new VideoBuilder();  // new VideoType();            $builder->image = $this->file;            $builder->buildContent();        }        if ($file->isAudio()) {            //$type = new AudioType();            $builder = new AudioBuilder();  // new VideoBuilder();  // new VideoType();            $builder->audio = $this->file;            $builder->buildContent();        }        if ($file->isVideo()) {            $builder = new VideoBuilder();  // new VideoType();            $builder->video = $this->file;            $builder->buildContent();        }        //$type->file = $this->file;        //$type->saveType();        //return $type;    }}