<?phpnamespace Nemundo\Content\App\File\Content\Upload;use Nemundo\Content\App\File\Content\Audio\AudioType;use Nemundo\Content\App\File\Content\Document\DocumentType;use Nemundo\Content\App\File\Content\File\FileContentType;use Nemundo\Content\App\File\Content\Image\ImageType;use Nemundo\Content\App\File\Content\Video\VideoType;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\File\FileInformation;use Nemundo\Model\Data\Property\File\FileProperty;class UploadFile extends AbstractBase{    /**     * @var FileProperty     */    public $file;    public $parentId;    public function __construct()    {        $this->file = new FileProperty();    }    public function uploadFile()    {        if ($this->file->hasFilename()) {            $filename = $this->file->getFilename();        }        if ($this->file->hasUrl()) {            $filename = $this->file->getUrl();        }        if ($this->file->hasFileRequest()) {            $filename = $this->file->getFileRequest()->filename;        }        $file = new FileInformation($filename);        $type = new FileContentType();        if ($file->isText()) {            $type = new DocumentType();        }        if ($file->isWord()) {            $type = new DocumentType();        }        if ($file->isPdf()) {            $type = new DocumentType();        }        if ($file->isImage()) {            $type = new ImageType();        }        if ($file->isAudio()) {            $type = new AudioType();        }        if ($file->isVideo()) {            $type = new VideoType();        }        $type->file = $this->file;        $type->saveType();        return $type;    }}