<?phpnamespace Nemundo\Content\App\File\Content\Video\Form;use Nemundo\Admin\Com\ListBox\AdminFileUpload;use Nemundo\Content\App\File\Content\Video\VideoBuilder;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Html\Form\Input\AcceptFileType;class VideoForm extends AbstractContentForm{    /**     * @var AdminFileUpload     */    private $video;    public function getContent()    {        $this->video = new AdminFileUpload($this);        $this->video->label = 'Video';        $this->video->validation = true;        $this->video->multiUpload = false;        $this->video->acceptFileType = AcceptFileType::VIDEO;        return parent::getContent();    }    protected function onSave()    {        $builder = new VideoBuilder();        $builder->video->fromFileRequest($this->video->getFileRequest());        $builder->addActionList($this->getActionList());        $builder->buildContent();    }}