<?phpnamespace Nemundo\Content\App\File\Content\Video;use Nemundo\Admin\Com\Player\AdminVideoPlayer;use Nemundo\Content\View\AbstractContentView;class VideoView extends AbstractContentView{    /**     * @var VideoType     */    //public $contentType;    protected function loadView()    {        $this->viewName = 'default';        $this->viewId = 'ecf82d1b-bbbf-44ba-805d-a4ca28caeaed';        $this->defaultView = true;    }    public function getContent()    {        $fileRow = (new VideoItem($this->dataId))->getDataRow();        $video = new AdminVideoPlayer($this);        $video->src = $fileRow->video->getUrl();        return parent::getContent();    }}