<?phpnamespace Nemundo\Content\App\FileTransfer\Page;use Nemundo\Admin\Com\Button\AdminButton;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Admin\Com\Table\AdminTableHeader;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\Com\Html\Hyperlink\UrlHyperlink;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Content\App\FileTransfer\Content\File\FileForm;use Nemundo\Content\App\FileTransfer\Data\File\FileReader;use Nemundo\Content\App\FileTransfer\Parameter\FileParameter;use Nemundo\Content\App\FileTransfer\Site\FileDeleteSite;class FileTransferPage extends AbstractTemplateDocument{    public function getContent()    {        $form = new FileForm($this);        //(new FileTransferContentType())->getDefaultForm($this);        /*$dropzone = new DropzoneUploadForm($this);        $dropzone->uploadSite = clone(FileTransferFileUploadSite::$site);        $dropzone->uploadSite->addParameter(new FileTransferParameter($fileTransferId));        $p = new Paragraph($this);        $p->content = 'Nach Upload Browser aktualisieren, um die Files zu sehen.';*/        $table = new AdminTable($this);        $header = new AdminTableHeader($table);        $header->addText('File');        $header->addEmpty();        $reader = new FileReader();        //$reader->filter->andEqual($reader->model->fileTransferId, $fileTransferId);        $reader->addOrder($reader->model->file->fileName);        foreach ($reader->getData() as $fileRow) {            $row = new AdminTableRow($table);            //$row->addText($fileRow->file->getFilename());            //$row->addClickableUrl($fileRow->file->getUrl());            $hyperlink = new UrlHyperlink($row);            //$hyperlink->openNewWindow=true;            $hyperlink->content = $fileRow->file->getFilename();            $hyperlink->url = $fileRow->file->getUrl();            $hyperlink->download = true;            $site = clone(FileDeleteSite::$site);            $site->addParameter(new FileParameter($fileRow->id));            $row->addIconSite($site);        }        return parent::getContent();    }}