<?phpnamespace Nemundo\Content\App\ImageGallery\Content\ImageGallery\View;use Nemundo\Content\App\ImageGallery\Content\ImageGallery\ImageGalleryItem;use Nemundo\Content\App\ImageGallery\Data\Image\ImageReader;use Nemundo\Content\View\AbstractContentView;abstract class AbstractImageGalleryView extends AbstractContentView{    protected function getImageGalleryRow()    {        return (new ImageGalleryItem($this->dataId))->getDataRow();    }    protected function getImageReader()    {        $imageReader = new ImageReader();        $imageReader->filter->andEqual($imageReader->model->galleryId, $this->dataId);        $imageReader->addOrder($imageReader->model->id);        return $imageReader;    }}