<?phpnamespace Nemundo\Content\App\ImageGallery\Content\ImageGallery\View;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;class ImageGalleryStreamView extends AbstractImageGalleryView{    protected function loadView()    {        $this->viewName = 'Stream';        $this->viewId = 'bda47aa4-011c-4bee-8d83-e4842a4ea2b5';        $this->defaultView = false;    }    public function getContent()    {        foreach ($this->getImageReader()->getData() as $imageRow) {            $img = new BootstrapResponsiveImage($this);            $img->src = $imageRow->image->getImageUrl($imageRow->model->imageAutoSize800);        }        return parent::getContent();    }}