<?phpnamespace Nemundo\Content\App\Location\Site;use Nemundo\Content\App\Location\Data\Location\LocationReader;use Nemundo\Content\App\Location\Data\Tracking\TrackingReader;use Nemundo\Content\App\Location\Parameter\LocationParameter;use Nemundo\Core\Type\Geo\GeoCoordinateAltitude;use Nemundo\Geo\Kml\Document\KmlDocument;use Nemundo\Geo\Kml\Object\KmlLine;use Nemundo\Geo\Kml\Object\KmlMarker;use Nemundo\Geo\Kml\Site\AbstractKmlSite;//use Nemundo\Package\FontAwesome\Site\AbstractKmlIconSite;class TrackingKmlSite extends AbstractKmlSite // AbstractKmlIconSite{    /**     * @var LocationKmlSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Tracking Kml';        $this->url = 'tracking-kml';        TrackingKmlSite::$site=$this;    }    public function loadContent()    {        $kml=new KmlDocument();        /*$marker=new KmlMarker($kml);        $marker->label='tracking';        //$marker->description=$locationRow->description;        $marker->coordinate=$locationRow->geoCoordinate;*/        $line=new KmlLine($kml);        $reader = new TrackingReader();        $reader->addOrder($reader->model->id);        foreach ($reader->getData() as $trackingRow) {            $line->addPoint((new GeoCoordinateAltitude())->fromGeoCoordinate( $trackingRow->geoCoordinate));        }        $kml->render();    }}