<?phpnamespace Nemundo\Content\App\Note\Content\Note;use Nemundo\Content\App\Note\Data\Note\Note;use Nemundo\Content\App\Note\Data\Note\NoteDelete;use Nemundo\Content\App\Note\Data\Note\NoteReader;use Nemundo\Content\App\Note\Data\Note\NoteRow;use Nemundo\Content\App\Note\Data\Note\NoteUpdate;use Nemundo\Content\Type\AbstractContentBuilder;use Nemundo\Content\Type\AbstractContentType;use Nemundo\Content\Type\JsonContentTrait;class NoteBuilder extends AbstractContentBuilder{    public $title;    public $text;    protected function loadBuilder()    {        $this->contentType=new NoteType();    }    protected function onCreate()    {        $data = new Note();        $data->title = $this->title;        $data->text = $this->text;        $this->dataId = $data->save();    }    protected function onUpdate()    {        $update = new NoteUpdate();        $update->title = $this->title;        $update->text = $this->text;        $update->updateById($this->dataId);    }    protected function onDataRow()    {        $this->dataRow = (new NoteReader())->getRowById($this->dataId);    }    /**     * @return \Nemundo\Model\Row\AbstractModelDataRow|NoteRow     */    public function getDataRow()    {        return parent::getDataRow();    }    public function getSubject()    {        return $this->getDataRow()->title;    }    public function getData()    {        $data = [];        $data['id'] = $this->getDataRow()->id;        $data['title'] = $this->getDataRow()->title;        $data['text'] = $this->getDataRow()->text;        return $data;    }}