<?phpnamespace Nemundo\Content\App\Note\Content\Note;use Nemundo\Admin\Com\ListBox\AdminHtmlEditor;use Nemundo\Admin\Com\ListBox\AdminTextBox;use Nemundo\Content\App\Note\Data\Note\NoteModel;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Package\Bootstrap\FormElement\BootstrapLargeTextBox;use Nemundo\Package\Bootstrap\FormElement\BootstrapTextBox;class NoteForm extends AbstractContentForm{    /**     * @var BootstrapTextBox     */    private $noteTitle;    /**     * @var BootstrapLargeTextBox     */    private $text;    public function getContent()    {        $model = new NoteModel();        $this->noteTitle = new AdminTextBox($this);        $this->noteTitle->label = $model->title->label;        $this->noteTitle->validation = true;        $this->text = new AdminHtmlEditor($this);        $this->text->label = $model->text->label;        return parent::getContent();    }    protected function loadUpdateForm()    {        $noteRow = (new NoteItem($this->dataId))->getDataRow();        $this->noteTitle->value = $noteRow->title;        $this->text->value = $noteRow->text;    }    protected function onSave()    {        $builder = new NoteBuilder($this->dataId);        $builder->title = $this->noteTitle->getValue();        $builder->text = $this->text->getValue();        $builder->addActionList($this->getActionList());        $builder->buildContent();    }}