<?phpnamespace Nemundo\Content\App\Note\Content\Note;use Nemundo\Admin\Com\Title\AdminSubtitle;use Nemundo\Content\View\AbstractContentView;use Nemundo\Core\Type\Text\Html;use Nemundo\Html\Block\ContentDiv;use Nemundo\Html\Paragraph\Paragraph;class NoteView extends AbstractContentView{    /**     * @var NoteType     */    public $contentType;    protected function loadView()    {        $this->viewName='default';        $this->viewId='417afbe8-fb9f-45ac-9710-a1e3a9e29934';        $this->defaultView=true;    }    public function getContent()    {        $noteRow = (new NoteItem($this->dataId))->getDataRow();        $subtitle = new AdminSubtitle($this);        $subtitle->content = $noteRow->title;        $div = new ContentDiv($this);        $div->content = $noteRow->text;        return parent::getContent();    }}