<?phpnamespace Nemundo\Content\App\Print\Page;use Nemundo\Content\Builder\ContentBuilder;use Nemundo\Content\Parameter\ContentParameter;use Nemundo\Html\Document\HtmlDocument;use Nemundo\Html\Header\Link\StylesheetLink;use Nemundo\Html\Header\Title;use Nemundo\Html\Heading\H1;use Nemundo\Html\Script\JavaScript;class ContentPrintPage extends HtmlDocument{    public function getContent()    {        $style= new StylesheetLink($this);        $style->href='/package/css/framework/print/print.css';        $script = new JavaScript($this);        $script->addCodeLine('window.print();');        $parameter = new ContentParameter();        $contentId = $parameter->getValue();        $builder = new ContentBuilder($contentId);        $title = new Title($this);        $title->content = $builder->getContentItem()->getSubject();        $h1 = new H1($this);        $h1->content = $builder->getContentItem()->getSubject();        $builder->getDefaultView($this);        return parent::getContent();    }}