<?phpnamespace Nemundo\Content\App\Print\Site;use Nemundo\Admin\Site\AbstractPrintSite;use Nemundo\Content\App\Print\Page\ContentPrintPage;class ContentPrintSite extends AbstractPrintSite{    /**     * @var ContentPrintSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Print';        $this->url = 'content-print';        $this->menuActive = false;        ContentPrintSite::$site = $this;    }    public function loadContent()    {        (new ContentPrintPage())->render();    }}