<?phpnamespace Nemundo\Content\App\PublicShare\Install;use Nemundo\App\Application\Setup\ApplicationSetup;use Nemundo\App\Application\Type\Install\AbstractInstall;use Nemundo\Content\Action\Setup\ActionSetup;use Nemundo\Content\App\PublicShare\Action\PublicShareContentAction;use Nemundo\Content\App\PublicShare\Application\PublicShareApplication;use Nemundo\Content\App\PublicShare\Data\PublicShareModelCollection;use Nemundo\Model\Setup\ModelCollectionSetup;class PublicShareInstall extends AbstractInstall{    public function install()    {        (new ApplicationSetup())            ->addApplication(new PublicShareApplication());        (new ModelCollectionSetup())            ->addCollection(new PublicShareModelCollection());        (new ActionSetup())            ->addContentAction(new PublicShareContentAction());    }}