<?phpnamespace Nemundo\Content\App\PublicShare\Install;use Nemundo\App\Application\Type\Install\AbstractUninstall;use Nemundo\Content\Action\Setup\ActionSetup;use Nemundo\Content\App\PublicShare\Action\PublicShareContentAction;use Nemundo\Content\App\PublicShare\Data\PublicShareModelCollection;use Nemundo\Model\Setup\ModelCollectionSetup;class PublicShareUninstall extends AbstractUninstall{    public function uninstall()    {        (new ModelCollectionSetup())            ->removeCollection(new PublicShareModelCollection());        (new ActionSetup())            ->removeContentAction(new PublicShareContentAction());    }}